            robots:robots,
            searchfield: ''
        }
    }
    onSearchChange=(event)=>
    {
        this.setState({searchfield:event.target.value})


    }
render()
{
    const filteredRobots=this.state.robots.filter(robots=>
        {return
             robots.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
         ;
        })

{
    return(
        <div className='tc'>
            <h1>Robofriends</h1>
